html_str_head='''
<html>
<head><title>HiKoB Memory Map Manager</title></head>
<body onload="createGUI()">
'''

##
##
##

html_str_body_1='''
<div class="divBox" style="position: relative; width: 100%; margin-top: 3px">
	<button style="right: 0px;" id="backButton">Back</button>
	<span id="infoBox"></span>
</div>

<div class="divBox" id="myDiv" style="height: 80%; width: 100%; background: #666666"></div>

<script type="text/javascript">

function createGUI()
{
        var rootNode = 
'''

html_str_body_2='''
        // Render the DIV treemap
		var mapDIV = document.getElementById( "myDiv" );
		var map = new DivTreeMap( mapDIV, rootNode, {
			shader: new TreeNodeShader( rootNode )
		} );
		
		// Set up surrounding GUI controls
		var infoBox = document.getElementById( "infoBox" );
		var backButton = document.getElementById( "backButton" );
		backButton.disabled = true;
		
		map.onMouseOver = function( node ) {
			infoBox.innerHTML = node.toString();
		}; 
		map.onMouseOut = function( node ) {
			infoBox.innerHTML = "";
		}; 
		map.onZoomClick = function( node ) {
			map.zoom();
			backButton.disabled = false;
		}
		
		backButton.onclick = function() {
			backButton.disabled = ( 0 === map.unzoom() );
		};
	
		// Define & plug in a resize hook
		window.onresize = function() { map.checkResize(); };
		map.checkResize();
}

function attrToString( attr )
{
	return attr.nodeName + "=" + attr.nodeValue;
}
</script>

</body>
</html>
'''

##
##
##

def html(tmap,title):
    lines = []
    
    for l in html_str_head.split('\n'):
        lines.append(l)
    lines.append('<h2>' + title + '</h2>')    
    lines.append('<script language="JavaScript">')
    lines.append(js_treemap_src)
    lines.append('</script>')
    
    for l in html_str_body_1.split('\n'):
        lines.append(l)
    #for l in tmap.split('\n'):
    #    lines.append(l)
    lines = lines + tmap
    for l in html_str_body_2.split('\n'):
        lines.append(l)
    return lines

